// Helper functions
func statusClass(host Host) string {
	if host.PendingCommand != "" {
		return "status-running"
	}
	if host.Online {
		return "status-online"
	}
	return "status-offline"
}

func hostColorStyle(host Host) string {
	if host.ThemeColor != "" {
		return "color: " + host.ThemeColor
	}
	return ""
}

func valueOrDash(v string) string {
	if v == "" {
		return "—"
	}
	return v
}

func formatPercent(v float64) string {
	return strconv.FormatFloat(v, 'f', 0, 64) + "%"
}

func formatLoad(v float64) string {
	return strconv.FormatFloat(v, 'f', 2, 64)
}

func metricsClass(metricType string, value float64) string {
	base := "metric " + metricType
	if value >= 80 {
		return base + " high"
	}
	return base
}

// updateCompartmentClass returns CSS classes for an update status compartment
func updateCompartmentClass(status *UpdateStatus, compartment string) string {
	base := "update-compartment"
	if status == nil {
		return base + " unknown"
	}

	var check StatusCheck
	switch compartment {
	case "git":
		check = status.Git
	case "lock":
		check = status.Lock
	case "system":
		check = status.System
	}

	switch check.Status {
	case "ok":
		return base
	case "outdated":
		return base + " needs-update"
	case "error":
		return base + " error"
	default:
		return base + " unknown"
	}
}

// updateCompartmentTitle returns the tooltip for an update status compartment
func updateCompartmentTitle(status *UpdateStatus, compartment string, hostType string) string {
	if status == nil {
		switch compartment {
		case "git":
			return "Git: Checking..."
		case "lock":
			return "Lock: Checking..."
		case "system":
			if hostType == "macos" {
				return "Home Manager: Checking..."
			}
			return "System: Checking..."
		}
		return "Checking..."
	}

	var check StatusCheck
	var label string
	switch compartment {
	case "git":
		check = status.Git
		label = "Git"
		// Build Git-specific tooltip with repo info
		tooltip := label
		if check.Message != "" {
			tooltip += ": " + check.Message
		} else {
			switch check.Status {
			case "ok":
				tooltip += ": Up to date"
			case "outdated":
				tooltip += ": Needs attention"
			case "error":
				tooltip += ": Error"
			default:
				tooltip += ": Checking..."
			}
		}
		// Add repo paths
		if status.RepoURL != "" {
			tooltip += " | Remote: " + status.RepoURL
		}
		if status.RepoDir != "" {
			tooltip += " | Local: " + status.RepoDir
		}
		return tooltip
	case "lock":
		check = status.Lock
		label = "Lock"
	case "system":
		check = status.System
		if hostType == "macos" {
			label = "Home Manager"
		} else {
			label = "System"
		}
	}

	if check.Message != "" {
		return label + ": " + check.Message
	}

	switch check.Status {
	case "ok":
		return label + ": Up to date"
	case "outdated":
		return label + ": Needs attention"
	case "error":
		return label + ": Error"
	default:
		return label + ": Unknown"
	}
}

