// P1020: getCompartmentActionType returns action type for routing
func getCompartmentActionType(status *UpdateStatus, compartment string) string {
	if status == nil {
		return "refresh"
	}

	var check StatusCheck
	switch compartment {
	case "git":
		check = status.Git
		switch check.Status {
		case "ok":
			return "refresh"
		case "outdated":
			return "pull"
		case "error":
			return "error"
		default:
			return "refresh"
		}
	case "system":
		check = status.System
		switch check.Status {
		case "ok":
			return "refresh"
		case "outdated":
			return "switch"
		case "error":
			return "error"
		default:
			return "refresh"
		}
	}
	return "refresh"
}

